function loadCarouselImages(){const e=new XMLHttpRequest;e.open("GET","lib/IMGViewer.xml",!0),e.onload=function(){if(200===this.status){const e=this.responseXML,t=e.getElementsByTagName("image");Array.from(t).forEach(e=>{let t;const n=e.getElementsByTagName("alt")[0].textContent;t=window.matchMedia("(max-width: 600px)").matches?e.querySelector('size[size="450x450"] src').textContent:e.querySelector('size[size="600x450"] src').textContent;const r=document.createElement("div");r.classList.add("carousel-slide");const c=document.createElement("img");c.src=t,c.alt=n,r.appendChild(c),carouselTrack.appendChild(r)}),updateCarousel(currentScreenWidth>600?mediaQuerryChangeWidth:currentScreenWidth)}},e.send()}function updateCarousel(e){const t=document.querySelectorAll(".carousel-slide");document.getElementById("prevBtn").addEventListener("click",()=>{currentIndex=(currentIndex-1+t.length)%t.length;const n=-currentIndex*e;carouselTrack.style.transform=`translateX(${n}px)`}),document.getElementById("nextBtn").addEventListener("click",()=>{currentIndex=(currentIndex+1)%t.length;const n=-currentIndex*e;carouselTrack.style.transform=`translateX(${n}px)`})}let currentIndex=0;const carouselTrack=document.getElementById("carousel-track");let currentScreenWidth=window.innerWidth;loadCarouselImages();const mediaQuerryChangeWidth=600;window.addEventListener("resize",function(){const e=window.innerWidth;currentScreenWidth=e,updateCarousel(currentScreenWidth>mediaQuerryChangeWidth?mediaQuerryChangeWidth:currentScreenWidth)});